export const searchForHeader = () => {

    const headerSearch = document.querySelector('.header__search');
    const headerInput = document.querySelector('.header__input');
    const headerEscape = document.querySelector('.header__escape');
    const headerForm = document.querySelector('.header__form');
    const headerSidebar = document.querySelector('.header-sidebar');
    const headerMenu = document.querySelector('.header__menu');

    headerSearch.onclick = function () {
        setTimeout(() => {
            headerEscape.classList.add('header__escape_display');
            headerInput.classList.add('header__input_visibility');
        }, 300)

    }

    headerEscape.onclick = function () {
        headerInput.classList.remove('header__input_visibility');
        setTimeout(() => {
            headerEscape.classList.remove('header__escape_display');
        }, 300)
        headerForm.reset();
    }

    headerSidebar.onclick = function () {
        headerSidebar.classList.toggle('close-header-sidebar');

        headerMenu.classList.toggle('show-menu');
    }

}

